package java_from_0;

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//for loop
		for(int i=0; i<=5;i++) {
			
			System.out.println("for loop " + i);
		}
		
		//while loop
		
		int j=0;
		while(j<=5) {
			System.out.println("while loop");
			j++;
		}
		
		
		//do while
		
		int g = 0;
		
		do {
			System.out.println("i am do while");
			g++;
		}while(g<=5);
		

	}

}
